  `PiFIsValid` int(11) DEFAULT '1' COMMENT '是否是有效成员（1：有效；0：无效）',
  `PiFJoinTime` datetime DEFAULT NULL COMMENT '成员加入时间',
  `PiFUserId` int(11) DEFAULT '0' COMMENT '用户id',
